mapper.update:

update feed_data set is_latest_version = false
where event_id = #{eventId}
  and feed_id = #{feedId}
  and version < #{version}
  and is_latest_version is true

mapper.select:
SELECT max(version)
FROM feed_data
WHERE event_id = #{eventId}
    AND feed_id = #{feedId}

mapper.update:
UPDATE feed_data SET
event_details = #{eventDetails}::jsonb,
enriched_at = current_timestamp, enriched = #{enriched}, episodes = #{episodes}::jsonb, name = #{name}, enrichment_attempts = #{enrichmentAttempts}, enrichment_skipped = #{enrichmentSkipped} WHERE feed_id = #{feedId} AND event_id = #{eventId} AND version = #{version}
